import { useState } from "react";
import "./styles.css";

const faqs = [
  {
    title: "Where are these chairs assembled?",
    text:
      "Lorem ipsum dolor sit amet consectetur, adipisicing elit. Accusantium, quaerat temporibus quas dolore provident nisi ut aliquid ratione beatae sequi aspernatur veniam repellendus."
  },
  {
    title: "How long do I have to return my chair?",
    text:
      "Pariatur recusandae dignissimos fuga voluptas unde optio nesciunt commodi beatae, explicabo natus."
  },
  {
    title: "Do you ship to countries outside the EU?",
    text:
      "Excepturi velit laborum, perspiciatis nemo perferendis reiciendis aliquam possimus dolor sed! Dolore laborum ducimus veritatis facere molestias!"
  }
];

export default function Accordion() {
    const [active, setActive] = useState(null);

    return (
        <>
            <div className="accordion">
                <ul className="content-box">
                {faqs.map((eachFaqs, index) => (
                    <Item   faqs={eachFaqs}
                            index={index}
                            setActive={setActive}
                    />
                ))}
                </ul>
            </div>
        </>
    )
}

function Item({faqs, index}){
    return(
        <>
            <li className="item open" onClick={setActive(index)}>
                <h3 className="number">{index + 1}</h3>
                <h3 className="title">{faqs.title}</h3>
                <h3 className="icon">{"+"}</h3>
                <p className="content-box">{faqs.text}</p>
            </li>
        </>
    )
}
