import React from "react";
import ReactDOM from "react-dom/client";
import { StrictMode } from "react";
import "./style.css";

const skillData = [
  {
    skill: "Sword",
    emoji: "‚öîÔ∏è",
    level: "advance",
    color: "#B03A2E",
  },
  {
    skill: "Axe",
    emoji: "ü™ì",
    level: "Intermediate",
    color: "#1A5276",
  },
  {
    skill: "Bow",
    emoji: "üèπ",
    level: "beginner",
    color: "#0E6655",
  },
  {
    skill: "Shield",
    emoji: "üõ°Ô∏è",
    level: "Intermediate",
    color: "#B7950B",
  },
  {
    skill: "Magic",
    emoji: "‚òÑÔ∏è",
    level: "Intermediate",
    color: "#5D6D7E",
  },
  {
    skill: "Summon",
    emoji: "ü¶Ñ",
    level: "beginner",
    color: "#A569BD",
  },
];

function App() {
  return (
    <div className="card">
      <Avatar image="avatar/sigma-men.jpg" />

      <div className="data">
        <Intro />
        <SkillList />
      </div>
    </div>
  );
}

function Avatar(props) {
  return (
    <>
      <img
        className="avatar"
        src={props.image}
        alt={props.image.substring(props.image.lastIndexOf("/") + 1)}
        width={500}
      ></img>
    </>
  );
}

function Intro() {
  return (
    <>
      <h1>The Black Worrior</h1>
      <p>
        A "brave man" refers to an individual who displays courage and
        fearlessness in the face of challenges, danger, or adversity.
      </p>
    </>
  );
}

function SkillList() {
  const recievedSkill = skillData;

  return (
    <ul className="skill-list">
      {skillData.map((skillObj, index) => (
        <Skill />
      ))}
    </ul>
  );

  /*<Skill skill="Sword" emoji="‚öîÔ∏è" color="#B03A2E" />
      <Skill skill="Axe" emoji="ü™ì" color="#1A5276" />
      <Skill skill="Bow" emoji="üèπ" color="#0E6655" />
      <Skill skill="Shield" emoji="üõ°Ô∏è" color="#B7950B" />
      <Skill skill="Magic" emoji="‚òÑÔ∏è" color="#5D6D7E" />
      <Skill skill="Summon" emoji="ü¶Ñ" color="#A569BD" />*/
}

function Skill(props) {
  return (
    <div className="skill" style={{ backgroundColor: props.color }}>
      <span>{props.skill}</span>
      <span>{props.emoji}</span>
    </div>
  );
}

// Render Zone
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <StrictMode>
    <App />
  </StrictMode>
);
