import { useState } from "react";

export default function TipCalculator(){

    const [theBill, setTheBill] = useState(null);
    const [mySatisfaction, setMySatisfaction] = useState(null);
    const [friendSatisfaction, setFriendSatisfaction] = useState(null);

    return(
        <>
            <Heading/>
            <Bill 
                    theBill={theBill} 
                    setTheBill={setTheBill}
            />
            <PercentSatisfaction
                    mySatisfaction={mySatisfaction}
                    setMySatisfaction={setMySatisfaction}>
                    How did you like the service? &nbsp;
            </PercentSatisfaction>
            <PercentSatisfaction
                    friendSatisfaction={friendSatisfaction}
                    setFriendSatisfaction={setFriendSatisfaction}>
                    How did your friend like the service? &nbsp;</PercentSatisfaction>
            <Calculation/>
            <Reset/>
        </>  
    )  
}

function Heading(){
    return(
        <div>
            <h3>Tip Calculator</h3>
            <br/>
        </div>
    )
}

function Bill({theBill, setTheBill}) {
    return (
        <div> 
            <p>How much was the bill? &nbsp; 
                <input type="text" value={theBill} onChange={(e)=>setTheBill(Number(e.target.value()))}/>
            </p>
        </div>
    );
}

function PercentSatisfaction({children}){
    return(
        <> 
            <p>
            {children}
            <select>
                <option value="0%">Dissatisfied (0%)</option>
                <option value="5%">It was okay (5%)</option>
                <option value="10%">It was good (10%)</option>
                <option value="20%">Absolutely amazing! (20%)</option>
            </select>
            </p>
        </>
    )        
}

function Calculation(){
    return(
        <div>
            <br/>
            <p style={{fontWeight : 'bold'}}>You pay ${} (${}+ ${}tip)</p>
        </div>
    )
}

function Reset(){
    return(
        <div>
            <br/>
            <button style={{fontWeight : 'bold'}}>Reset</button>
        </div>
    )
}
