import { useEffect, useState } from "react";

export default function CurrencyConverter(){

    const [theValue, setTheValue] = useState(1)
    const [formUnit, setFormUnit] = useState(null);
    const [toUnit, setToUnit] = useState(null);
    const [theResult, setTheResult] = useState(0);

    useEffect(() => {
        async function Converter()
        {
            const response = await fetch(`https://api.frankfurter.app/latest?amount=${theValue}&from=${formUnit}&to=${toUnit}`);
            const data = await response.json();
            setTheResult(data.rates.USD);
        }
    
        Converter();
    },[theValue, formUnit, toUnit]);


    /*useEffect(() => {
            fetch(`https://api.frankfurter.app/latest?amount=${theValue}&from=GBP&to=USD`)
            .then(resp => resp.json())
            .then((data) => {
            setTheResult(data.rates.USD);
        });

    },[theValue]);*/

    
    
    // `https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD`

    return (
        <>

        <h3>Currency Converter</h3>
        <br/>

        <div>

            <input type="text" value={theValue} onChange={(e) => setTheValue(e.target.value)}/>

            <select value={formUnit} onChange={(e) => setFormUnit(e.target.value)}>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="CAD">CAD</option>
                <option value="INR">INR</option>
            </select>

            <select value={toUnit} onChange={(e) => setToUnit(e.target.value)}>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="CAD">CAD</option>
                <option value="INR">INR</option>
            </select>

          <p>{theResult}</p>

        </div>

        </>
      );
    }
