export default function TipCalculator(){
    return(
        <>
            <Heading/>
            <Form/>
        </>  
    )  
}

function Heading(){
    return(
        <>
            <h3>Tip Calculator</h3>
            <br/>
        </>
    )
}

function Form(){
    return(
        <>
            
            <p>How much was the bill?</p>
            <p>How did you like the service?</p>
            <p>How did you friend like the service?</p>
        </>
    )
}
