import { useReducer } from "react";
import "./styles.css";

export default function BankAccount(){

    const [{balance, loan, isActive}, dispatch] = useReducer((state,action)=>{
        switch(action.type){
            case '' : return{}
            case '' : return{}
            case '' : return{}
            default : throw Error("Action unknow")
        }
    },{
        balance: 0,
        loan: 0,
        isActive: false,
    });

    return(
        <>
            <h3>Bank Account</h3>

            <div className="App">

                <div>
                    <p>Balance: X</p>
                    <p>Loan: X</p>
                </div>

                <p><button>Open account</button></p>
                <p><button>Withdraw 50</button></p>

                <p><button>Request a loan of 5000</button></p>
                <p><button>Pay loan</button></p>
                
                <p><button>Cloase account</button></p>

            </div>
        </>
    )
}
