import React from "react";
import  ReactDOM  from "react-dom/client";
import "./style.css";

function App(){
    return (
        <div className="card">
            <Avatar image = "avatar/sigma-men.jpg"/>

            <div className="data">
                <Intro  word = 'A "brave man" refers to an individual who displays courage and fearlessness in the face of challenges, danger, or adversity.'/>
            </div>
        
        
        
        
        </div>
    )
}

function Avatar(props){
    return(
        <>
            <img className="avatar" src={props.image} alt={props.image.substring(props.image.lastIndexOf('/') + 1)} width={500}></img>
        </>
    )
}

function Intro(props){
    return(
        <>
            <p>{props.word}</p>
        </>
    )
}


// Render Zone
const root = ReactDOM.createRoot(document.getElementById("root"))
root.render(
    <App />
)
