import { useState } from "react";
import "./style.css";

export default function TipCalculator(){

    const [theBill, setTheBill] = useState(0);
    const [mysatiSfaction, setmysatiSfaction] = useState(0);
    const [FriendSatisfaction, setFriendSatisfaction] = useState(0);


    return(
        <>
            <Heading/>
            <Bill
                    theBill={theBill} 
                    setTheBill={setTheBill}/>
            <PercentSatisfaction
                    mySatisfaction={mySatisfaction}
                    setMySatisfaction={setMySatisfaction}></PercentSatisfaction>
            <PercentSatisfaction
                    mySatisfaction={mySatisfaction}
                    setMySatisfaction={setMySatisfaction}></PercentSatisfaction>
            <Calculation
                    theBill={theBill}
                    mySatisfaction={mySatisfaction}
                    friendSatisfaction={friendSatisfaction}/>
            <Reset
                    setTheBill={setTheBill}
                    setMySatisfaction={setMySatisfaction}
                    setFriendSatisfaction={setFriendSatisfaction}/>
        </>  
    )  
}

function Heading(){
    return(
        <div>
            <h3>Tip Calculator</h3>
            <br/>
        </div>
    )
}

function Bill({theBill, setTheBill}) {
    return (
        <div> 
            <p>How much was the bill? &nbsp; 
                <input type="text" value={theBill} onChange={(e)=>setTheBill(Number(e.target.value))}/>
            </p>
        </div>
    );
}

function MyPercentSatisfaction({ mySatisfaction, setMySatisfaction }) {
    return (
        <div>
            <p>
                How did you like the service? &nbsp;
                <select value={mySatisfaction} onChange={(e) => setMySatisfaction(Number(e.target.value))}>
                    <option value={0}>Dissatisfied (0%)</option>
                    <option value={5}>It was okay (5%)</option>
                    <option value={10}>It was good (10%)</option>
                    <option value={20}>Absolutely amazing! (20%)</option>
                </select>
            </p>
        </div>
    );
}


function FriendPercentSatisfaction({friendSatisfaction, setFriendSatisfaction}){
    return(
        <> 
            <p>
            How did your friend like the service? &nbsp;
            <select value={friendSatisfaction} onChange={(e)=>setFriendSatisfaction(Number(e.target.value))}>
                <option value={0}>Dissatisfied (0%)</option>
                <option value={5}>It was okay (5%)</option>
                <option value={10}>It was good (10%)</option>
                <option value={20}>Absolutely amazing! (20%)</option>
            </select>
            </p>
        </>
    )        
}

function Calculation({theBill,mySatisfaction,friendSatisfaction}){
    const tip = (theBill*((mySatisfaction+friendSatisfaction)/2))/100
    const total = theBill + tip

    return(
        <div>
            <br/>
            <p style={{fontWeight : 'bold'}}>You pay ${total} (${theBill} + ${tip} tip)</p>
        </div>
    )
}

function Reset({setTheBill, setMySatisfaction, setFriendSatisfaction}){

    function resetAll(){
        setTheBill(0);
        setMySatisfaction(0);
        setFriendSatisfaction(0);
    }

    return(
        <div>
            <br/>
            <button style={{fontWeight : 'bold'}} onClick={()=>resetAll()}>Reset</button>
        </div>
    )
}
