import React from "react";
import ReactDOM from "react-dom/client";
import { StrictMode } from "react";
import "./style.css";

const skillData = [
  {
    skill: "Sword",
    emoji: "‚öîÔ∏è",
    level: "advanced",
    color: "#B03A2E",
  },
  {
    skill: "Axe",
    emoji: "ü™ì",
    level: "Intermediate",
    color: "#1A5276",
  },
  {
    skill: "Bow",
    emoji: "üèπ",
    level: "beginner",
    color: "#0E6655",
  },
  {
    skill: "Shield",
    emoji: "üõ°Ô∏è",
    level: "advanced",
    color: "#B7950B",
  },
  {
    skill: "Magic",
    emoji: "‚òÑÔ∏è",
    level: "Intermediate",
    color: "#5D6D7E",
  },
  {
    skill: "Summon",
    emoji: "ü¶Ñ",
    level: "beginner",
    color: "#A569BD",
  },
];

function App() {
  return (
    <div className="card">
      <Avatar image="avatar/sigma-men.jpg" />

      <div className="data">
        <Intro />
        <SkillList />
      </div>
    </div>
  );
}

function Avatar(props) {
  return (
    <>
      <img
        className="avatar"
        src={props.image}
        alt={props.image.substring(props.image.lastIndexOf("/") + 1)}
        width={500}
      ></img>
    </>
  );
}

function Intro() {
  return (
    <>
      <h1>The Black Worrior</h1>
      <p>
        A "brave man" refers to an individual who displays courage and
        fearlessness in the face of challenges, danger, or adversity.
      </p>
    </>
  );
}

function SkillList() {
  return (
    <>
      {skillData.length > 0 ? (
        <ul className="skill-list">
          {skillData.map((eachSkill, index) => (
            <Skill
              skill={eachSkill.skill}
              emoji={eachSkill.emoji}
              level={eachSkill.emoji}
              color={eachSkill.emoji}
              key={index}
            />
          ))}
        </ul>
      ) : (
        <p>Data not found</p>
      )}
    </>
  );
}

function Skill({ skillObj }) {
  return (
    <li className="skill" style={{ backgroundColor: skillObj.color }}>
      <span>{skillObj.emoji}</span>
      <span>{skillObj.skill}</span>
      <span>
        [
        {skillObj.level === "advanced"
          ? "üí™"
          : skillObj.level === "Intermediate"
          ? "üëç"
          : "üë∂"}
        ]
      </span>
    </li>
  );
}

// Render Zone
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <StrictMode>
    <App />
  </StrictMode>
);
