import { useState } from "react";

export default function DateCounter() {
  const [step, setStep] = useState(1);
  const [count, setCount] = useState(0);

  function handleSetStep(increase) {
    whichButton ? setStep((s) => s + 1) : setStep((s) => s - 1);
  }

  function handleSetCount() {}

  const date = new Date();

  return (
    <>
      <div>
        <button
          onClick={() => handleSetStep(false)}
          style={{ fontSize: 30, width: 40, height: 40 }}
        >
          -
        </button>
        <span> Step:{step} </span>
        <button
          onClick={() => handleSetStep(true)}
          style={{ fontSize: 30, width: 40, height: 40 }}
        >
          +
        </button>
      </div>
      <div>
        <button
          onClick={handleSetCount}
          style={{ fontSize: 30, width: 40, height: 40 }}
        >
          -
        </button>
        <span> Count:{count} </span>
        <button
          onClick={handleSetCount}
          style={{ fontSize: 30, width: 40, height: 40 }}
        >
          +
        </button>
      </div>
      <div>
        <p style={{ fontSize: 40 }}>{date.toLocaleDateString()}</p>
      </div>
    </>
  );
}
