import React, { useState } from "react";
import ReactDOM from "react-dom/client";
import { StrictMode } from "react";
import "./style.css";

const skillData = [
  {
    skill: "Sword",
    emoji: "‚öîÔ∏è",
    level: "advanced",
    color: "#B03A2E",
  },
  {
    skill: "Axe",
    emoji: "ü™ì",
    level: "Intermediate",
    color: "#1A5276",
  },
  {
    skill: "Bow",
    emoji: "üèπ",
    level: "beginner",
    color: "#0E6655",
  },
  {
    skill: "Shield",
    emoji: "üõ°Ô∏è",
    level: "advanced",
    color: "#B7950B",
  },
  {
    skill: "Magic",
    emoji: "‚òÑÔ∏è",
    level: "Intermediate",
    color: "#5D6D7E",
  },
  {
    skill: "Summon",
    emoji: "ü¶Ñ",
    level: "beginner",
    color: "#A569BD",
  },
];

function App() {
  return (
    <div className="app-container">
      <div className="card">
        <Avatar image="avatar/sigma-men.jpg" />

        <div className="data">
          <Intro />
          <SkillList />
        </div>
      </div>
      <div className="card">
        <OpenTheDoor />
      </div>
    </div>
  );
}

function OpenTheDoor() {
  const [doorState, setDoorState] = useState(true);

  return (
    <>
      <div>
        <img
          onClick={() => setDoorState((door) => !door)}
          src={doorState ? "avatar/dooropen.jpg" : "avatar/doorclose.jpg"}
          alt={doorState ? "dooropen" : "doorclose"}
          width={300}
        ></img>
      </div>
    </>
  );
}

// Render Zone
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <StrictMode>
    <App />
  </StrictMode>
);
