export default function TipCalculator(){


    return(
        <>
            <Heading/>
            <Bill ></Bill>
            <MySatisfaction></MySatisfaction>
            <FriendSatisfaction></FriendSatisfaction>
            <Calculation/>
            <Reset/>
        </>  
    )  
}

function Heading(){
    return(
        <div>
            <h3>Tip Calculator</h3>
            <br/>
        </div>
    )
}

function Bill() {
    return (
        <div> 
            <p>How much was the bill? &nbsp; 
                <input type="text" />
            </p>
        </div>
    );
}

function MySatisfaction(){
<p>How did you like the service? &nbsp; 
                <select>
                    <option value="0%">Dissatisfied (0%)</option>
                    <option value="5%">It was okay (5%)</option>
                    <option value="10%">It was good (10%)</option>
                    <option value="20%">Absolutely amazing! (20%)</option>
                </select>
            </p>
}
function FriendSatisfaction(){
    <p>How did your friend like the service? &nbsp; 
    <select>
        <option value="0%">Dissatisfied (0%)</option>
        <option value="5%">It was okay (5%)</option>
        <option value="10%">It was good (10%)</option>
        <option value="20%">Absolutely amazing! (20%)</option>
    </select>
</p>
}

function Calculation(){
    return(
        <div>
            <br/>
            <p style={{fontWeight : 'bold'}}>You pay ${} (${}+ ${}tip)</p>
        </div>
    )
}

function Reset(){
    return(
        <div>
            <br/>
            <button style={{fontWeight : 'bold'}}>Reset</button>
        </div>
    )
}
