import React from "react";
import  ReactDOM  from "react-dom/client";
import { StrictMode } from "react";
import "./style.css";

function App(){
    return (
        <div className="card">
            <Avatar image = "avatar/sigma-men.jpg"/>

            <div className="data">
                <Intro  word = 'A "brave man" refers to an individual who displays courage and fearlessness in the face of challenges, danger, or adversity.'/>
            
                <SkillList skill = "Sword ⚔️ " color = "#B03A2E"/>
                <SkillList skill = "Axe 🪓 " color = "#1A5276"/>
                <SkillList skill = "Bow 🏹 " color = "#0E6655"/>
                <SkillList skill = "Shield 🛡️ " color = "#B7950B"/>
                <SkillList skill = "Magic ☄️ " color = "#5D6D7E"/>
                <SkillList skill = "Summon 🦄 " color = "#A569BD"/>
            
            </div>

        </div>
    )
}

function Avatar(props){
    return(
        <>
            <img className="avatar" src={props.image} alt={props.image.substring(props.image.lastIndexOf('/') + 1)} width={500}></img>
        </>
    )
}

function Intro(props){
    return(
        <>
            <p>{props.word}</p>
        </>
    )
}

function SkillList(props){
    return(
        <>
            <div className="skill-List">
                <p className="skill" style={{backgroundColor: "#B03A2E"}}>{props.skill}</p>
            </div>
        </>
    )
}


// Render Zone
const root = ReactDOM.createRoot(document.getElementById("root"))
root.render(
    <StrictMode>
        <App />
    </StrictMode>
)
