import { useState } from "react";

export default function DateCounter() {
  const [step, setStep] = useState(1);
  const [count, setCount] = useState(0);

  return (
    <>
      <div>
        <button
          style={{ fontSize: 30, fontWeight: "bold", width: 40, height: 40 }}
        >
          -
        </button>
        <span>Step:{step}</span>
        <button
          style={{ fontSize: 30, fontWeight: "bold", width: 40, height: 40 }}
        >
          +
        </button>
      </div>
      <div>
        <button
          style={{ fontSize: 30, fontWeight: "bold", width: 40, height: 40 }}
        >
          -
        </button>
        <span>Count:{count}</span>
        <button style={{ fontSize: 30, width: 40, height: 40 }}>+</button>
      </div>
    </>
  );
}
