import { useEffect, useState } from "react";

export default function CurrencyConverter(){

    const [theValue, setTheValue] = useState(0)
    const [formUnit, setFormUnit] = useState("USD");
    const [toUnit, setToUnit] = useState("USD");

    useEffect(() => {

        async function Converter()
        {
            fetch("https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD")
        }

        Converter()
    },[theValue, formUnit, toUnit])
    
    // `https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD`

    return (
        <>

        <h3>Currency Converter</h3>
        <br/>

        <div>

            <input type="text" value={theValue} onChange={(e) => setTheValue(e.target.value)}/>

            <select value={formUnit} onChange={(e) => setFormUnit(e.target.value)}>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="CAD">CAD</option>
                <option value="INR">INR</option>
            </select>

            <select value={toUnit} onChange={(e) => setToUnit(e.target.value)}>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="CAD">CAD</option>
                <option value="INR">INR</option>
            </select>

          <p>OUTPUT</p>

        </div>

        </>
      );
    }
