import React from "react";
import  ReactDOM  from "react-dom/client";
import "./index.css";

const pizzaData = [
    {
      name: "Focaccia",
      ingredients: "Bread with italian olive oil and rosemary",
      price: 6,
      photoName: "pizzas/focaccia.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Margherita",
      ingredients: "Tomato and mozarella",
      price: 10,
      photoName: "pizzas/margherita.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Spinaci",
      ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
      price: 12,
      photoName: "pizzas/spinaci.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Funghi",
      ingredients: "Tomato, mozarella, mushrooms, and onion",
      price: 12,
      photoName: "pizzas/funghi.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Salamino",
      ingredients: "Tomato, mozarella, and pepperoni",
      price: 15,
      photoName: "pizzas/salamino.jpg",
      soldOut: true,
    },
    {
      name: "Pizza Prosciutto",
      ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
      price: 18,
      photoName: "pizzas/prosciutto.jpg",
      soldOut: false,
    },
  ];
  

function App () {
    return (
        <div className="container header">
            <Header />
            <Menu />
            <Footer />
        </div>
    );
}

function Header() {
    //const style = {color:'red', fontSize: "25px", textTransform: "uppercase"}
    const style = {}
    return (
        <header className="header">
            <h1 style={style} >Fast React Pizza Co.</h1>
        </header>
        );
}

function Menu() {
    return (
        <main className="menu">
            <h2>Our Menu</h2>
            {pizzaData.map((eachPizza, index) => (
                <Pizza
                    key={index} // Adding a unique key for each Pizza component
                    name={eachPizza.name}
                    ingredients={eachPizza.ingredients}
                    photoName={eachPizza.photoName}
                    price={eachPizza.price}
                />
            ))}
        </main>
    );
}

function Pizza() {
    return (    
        <div>
            <img src={props.photoName} alt={props.name} width={200} height={200}/>
            <h3>{props.name}</h3>
            <p>{props.ingredients}</p>
            <p>{props.price}</p>
        </div>
    );
}

function Footer() {
    const hour = new Date().getHours();
    const openHour = 8;
    const closeHour = 20;
    const isOpen = (openHour <= hour && hour <= closeHour);
    return <footer> {hour} {isOpen ? "We're Open": "We're close!!"} </footer>
}

// React v18
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
    <React.StrictMode>
    <App />
    </React.StrictMode>
);
