import { useState } from "react";

const messages = [
  "Learn React ‚öõÔ∏è",
  "Apply for jobs üíº",
  "Invest your new income ü§ë",
  "Be rich üí∞",
  "Be extreamly rich üí∞üí∞",
  "Be extreamly rich and successfull üí∞üí∞üèÜ",
  "Be extreamly rich and successfull and the greatest üí∞üí∞üèÜüèÜ",
  "Be extreamly rich and successfull and be the greatest and the powerful üí∞üí∞üèÜüèÜüëë",
];

export default function App() {

  const [step, setStep] = useState(1);

  function handlePrevious(){
    const newStep = step - 1
    setStep(newStep <= 0 ? 0 : newStep)
  }

  function handleNext(){
    const newStep = step + 1
    setStep(newStep >= messages.length ? messages.length - 1 : newStep)
  }

  return (
    <>
      <div className="steps">
        <div className="numbers">
          {
            messages.map((value, i)=><div className={step >= i && "active"}>{i+1}</div>)
          }

          {/*<div className={step >= 1 && "active"}>1</div>
          <div className={step >= 2 && "active"}>2</div>
          <div className={step >= 3 && "active"}>3</div>
          <div className={step >= 4 && "active"}>4</div>
          <div className={step >= 5 && "active"}>5</div>
          <div className={step >= 6 && "active"}>6</div>
          <div className={step >= 7 && "active"}>7</div>
        <div className={step >= 8 && "active"}>8</div>*/}
        </div>

        <p className="message">
          Step {step+1}:{messages[step]}
        </p>
        <div className="buttons">
          <button style={{ backgroundColor: "#7950f2", color: "#FFFFFF" }} onClick={handlePrevious}>Previous</button>
          <button style={{ backgroundColor: "#7950f2", color: "#FFFFFF" }} onClick={handleNext}>Next</button>
        </div>
      </div>
    </>
  );
}
