import { useState } from "react";

const messages = [
  "Learn React ‚öõÔ∏è",
  "Apply for jobs üíº",
  "Invest your new income ü§ë",
  "Be rich ü§ë",
  "Be more rich ü§ë",
  "Be more more rich ü§ë",
  "Be more more more rich ü§ë",
  "Be extremely rich ü§ë",
];

export default function App() {
  return (
    <div>
      <Step />
      {/*<Step />*/}
    </div>
  );
}

function Step() {
  const [step, setStep] = useState(0);
  const [emoji, setEmoji] = useState("");
  const [isOpen, setIsOpen] = useState(true);

  function handlePrevious() {
    if (step > 0) {
      setStep((s) => s - 1);
      setEmoji(emoji.slice(0, emoji.lastIndexOf("üí∞")));
    }
  }

  function handleNext() {
    if (step < messages.length - 1) {
      setStep((s) => s + 1);
      for (let i = 1; i <= step; i++) {
        setEmoji(emoji.concat("üí∞"));
      }
    }
  }

  return (
    <div>
      <button className="close" onClick={() => setIsOpen((isOp) => !isOp)}>
        &times;
      </button>

      {isOpen && (
        <div className="steps">
          <div className="numbers">
            {messages.map((_, i) => (
              <div className={step >= i ? "active" : ""} key={i}>
                {i + 1}
              </div>
            ))}
          </div>

          <p className="message">
            Step {step + 1}:{messages[step]}
            {emoji}
          </p>
          <div className="buttons">
              <Button bgColor='#7950f2'
                      textColor='#FFF'
                      onClick={handlePrevious}>
                      Next <span>üëàüèº</span>
              </Button>
              <Button bgColor='#7950f2'
                      textColor='#FFF'
                      onClick={handleNext}>
                      Next <span>üëâüèº</span>
              </Button>
              
          </div>
        </div>
      )}
    </div> //for IsOpen
  );
}


function Button({textColor, bgColor, onClick, children}) {
  return (
  <button 
    style={{ backgroundColor: bgColor, color: textColor }}
    onClick={onClick}>
    children
  </button>)
}
