import { useState } from "react";

const messages = [
  "Learn React ⚛️",
  "Apply for jobs 💼",
  "Invest your new income 🤑",
  "Be rich",
  "Be more rich",
  "Be extreamly rich and successfull",
  "Be extreamly rich and successfull and the greatest",
  "Be extreamly rich and successfull and be the greatest and the powerful",
];


export default function App() {

  const [step, setStep] = useState(0);
  const [emoji, setEmoji] = useState("")

  function handlePrevious(){
    if(step > 0){
      setStep(step - 1)
      for (let i = 1; i <= step; i++) {
        setEmoji(emoji - "💰");
      }
    } 
  }

  function handleNext(){
    if(step < messages.length -1) {
      setStep(step + 1)
      for (let i = 1; i <= step; i++) {
        setEmoji(emoji + "💰");
      }
    }
  }

  return (
    <>
      <div className="steps">
        <div className="numbers">
          {
            messages.map((_value, i)=><div className={step >= i && "active"}>{i+1}</div>)
          }

          {/*<div className={step >= 1 && "active"}>1</div>
          <div className={step >= 2 && "active"}>2</div>
          <div className={step >= 3 && "active"}>3</div>
          <div className={step >= 4 && "active"}>4</div>
          <div className={step >= 5 && "active"}>5</div>
          <div className={step >= 6 && "active"}>6</div>
          <div className={step >= 7 && "active"}>7</div>
        <div className={step >= 8 && "active"}>8</div>*/}
        </div>

        <p className="message">
          Step {step+1}:{messages[step]}{emoji}
        </p>
        <div className="buttons">
          <button style={{ backgroundColor: "#7950f2", color: "#FFFFFF" }} onClick={handlePrevious}>Previous</button>
          <button style={{ backgroundColor: "#7950f2", color: "#FFFFFF" }} onClick={handleNext}>Next</button>
        </div>
      </div>
    </>
  );
}
