const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: true },
  { id: 3, description: "Charger", quantity: 1, packed: false },
];

export default function App() {
  return(
    <div className="app">
      <Logo/>
      <Form/>
      <PackingList/>
      <State/>
    </div>
  );
}

function Logo(){
  return <h1>üå¥ Far Away</h1>
}

function Form(){
  return (
  <div className="add-form">
    <h3>üéÉ What do you need for your trip?</h3>
  </div>
  )
}

function PackingList(){
  return (
    <div className="list">
      <ul>
        {initialItems.map((eachItem,index) => (
        <Item item={eachItem} key={index}/>
        ))}
      </ul>
    </div>
  );
}

function Item({item}){
  return (
    <li>
      <span style={item.packed && {textDecoration : 'Line-through'}}>
        {item.quantity}&nbsp;
        {item.description}
      </span>
      <button>‚ùå</button>
    </li>
  )
}

function State(){
  return (
    <footer>
      <em>üé° You have X item on your list, and you already packed X (X%)</em>
    </footer>
  )
}
